export default function validateInfo(values) {
    let errors ={}

    if(!values.name.trim()){
        errors.name = "Polje ime je obavezno"
    }

    if(values.message > 1000){
        errors.message ="Broj slova je ogranicen na 1000"
    }
    return errors;
}
